mapper.insert:

insert into t_handleworkhistory (id, workorderid, handleperson,
handlepersonname, handlegroup, handlegroupname,
actionstr, createdate, descs
)
values (#{id,jdbcType=INTEGER}, #{workorderid,jdbcType=VARCHAR},
#{handleperson,jdbcType=INTEGER},
#{handlepersonname,jdbcType=VARCHAR}, #{handlegroup,jdbcType=INTEGER},
#{handlegroupname,jdbcType=VARCHAR},
#{actionstr,jdbcType=VARCHAR}, #{createdate,jdbcType=TIMESTAMP}, #{descs,jdbcType=VARCHAR}
)

mapper.select:
  select 
 DISTINCT workorderid,handleperson,handlepersonname,
   handlegroup,handlegroupname
 from t_handleworkhistory t
where t.workorderid=#{workid}
  and handleperson=#{userid}
